mapper.select:
select id, engine_number, engine_name, equipment_factory, engine_addr, engine_api, username, password, engine_capacity, engine_capacity_model, maxTask, activity, status, engine from cs_enginecfg e where 1=1 and status = 1
and engine_name = #{engineName}
and engine_name like '%${engineName}%'
and engine_addr like '%${engineAddr}%'
and equipment_factory = #{factory}
ORDER BY id
