mapper.select:
SELECT a.id orderId, a.serverName, b.astName, a.price, a.serviceId, a.isAPI, a.status, a.begin_date beginDate, a.end_date endDate FROM (SELECT co.*, cs.name serverName FROM cs_order co, cs_service cs WHERE co.serviceId = cs.id AND co.userId = #{userId} AND co.payFlag = #{payFlag} AND (co.isAPI = 0 or co.isAPI = 2)) a, (SELECT GROUP_CONCAT(ast.name) AS astName, cat.orderId FROM cs_asset ast, cs_order_asset cat WHERE ast.id IN(SELECT cas.assetId FROM cs_order_asset cas, cs_order co WHERE co.userId = #{userId} AND co.payFlag = #{payFlag} AND cas.orderId = co.id) AND ast.id = cat.assetId GROUP BY cat.orderId) b WHERE a.id = b.orderId
and a.id=#{orderId}
ORDER BY a.create_date desc

SELECT a.id orderId, a.serverName, a.price, b.package_type pack_type, a.serviceId, a.isAPI, a.status, b.buynum, a.begin_date beginDate, a.end_date endDate FROM (SELECT co.*, cs.name serverName FROM cs_order co, cs_service_api cs WHERE co.serviceId = cs.id AND co.userId =#{userId} AND co.payFlag = #{payFlag} AND co.isAPI = 1) a, (SELECT api.id orderId, api.package_type, api.buynum FROM cs_order_api api WHERE api.id in (SELECT co.id FROM cs_order co WHERE co.userId =#{userId} AND co.payFlag =#{payFlag} AND co.isAPI = 1)) b WHERE a.id = b.orderId
and a.id=#{orderId}
ORDER BY a.create_date desc

        SELECT s.*,c.name serviceName,GROUP_CONCAT(a.name) assetName,
 GROUP_CONCAT(a.addr) assetAddr from cs_settlemet s, cs_service c,cs_asset a
        
        WHERE s.userId = #{userId}
    AND s.id = #{id}
AND s.serviceId=c.id

