select.if:
		select
		a.id,a.name,
		a.level,a.alarm_content,a.alarm_type 
		from cs_alarm a,
		(select t.taskId,t.group_id from cs_order_asset oa,cs_task t
		where oa.id = t.order_asset_id
		and oa.orderId=#{orderId}
		and t.status = 3
		
			and t.group_flag=#{group_flag}
		 
		order by t.execute_time desc
		
			limit #{count}
		
		) b
		where (a.taskId = b.taskId or a.group_id = b.group_id)
		
			and a.level=#{level}
		
		
			and a.name=#{name}
		
		order by a.level desc 
		
             limit #{pageNow},#{pageSize}

			select id,name,DATE_FORMAT(alarm_time,'%Y-%m-%d %H:%i:%S') alarm_time,
			score,level,advice,alarm_content,url,keyword,a.taskid,alarm_type,
userid,group_id,serviceid,districtid from cs_alarm a, (select t.taskId from
			cs_order_asset
			oa,cs_task t where oa.id = t.order_asset_id
			and t.status = 3
			
				and t.group_flag=#{group_flag}
			
			order by t.execute_time desc
			
				limit #{count}
			
			) b where a.taskId = b.taskId and a.id=#{id}
			and a.level=#{level}
			and a.name=#{name}
			order by a.level desc

select a.id,a.name,DATE_FORMAT(a.alarm_time,'%Y-%m-%d %H:%i:%S') alarm_time, a.score,a.level,a.alarm_content,a.url,a.keyword,a.taskId, a.alarm_type,a.userId,a.group_id,l.advice from cs_alarm a, (select t.taskId,t.group_id from cs_order_asset oa,cs_task t where oa.id in (t.order_asset_id) and t.status = 3
and t.group_flag=#{group_flag}
order by t.execute_time desc
limit #{count}
) b,cs_leakinfo l where a.group_id = b.group_id and a.id=#{id} and a.name = l.name and a.level=#{level} and a.name=#{name} order by a.level desc
