mapper.select:
select b.id,b.type,b.sid,b.name,b.serviceId,DATE_FORMAT(b.begin_date,'%Y-%m-%d %H:%i:%S') begin_date ,DATE_FORMAT(b.end_date,'%Y-%m-%d %H:%i:%S') end_date, DATE_FORMAT(b.create_date,'%Y-%m-%d %H:%i:%S') create_date,b.scan_type,b.status,b.websoc from ( SELECT c.id,c.type,c.sid,c.name,c.serviceId,c.begin_date,c.end_date,c.create_date,c.scan_type,c.status,c.websoc FROM (SELECT o.id,o.type,s.id as sid,s.name,o.serviceId,o.scan_type,o.begin_date,o.end_date,o.create_date,o.status,o.websoc FROM cs_order o,cs_service s WHERE o.userId = #{userId} AND o.serviceId = s.id) c WHERE 1=1
AND c.begin_date > #{currentDate}
AND c.begin_date <= #{currentDate} AND c.status in (0,3)
AND c.status in (1,2)
) b order by b.create_date desc
limit #{pageNow},#{pageSize}

select b.id,b.type,b.sid,b.name,b.serviceId,b.begin_date,b.end_date,b.create_date,b.scan_type,b.status from ( SELECT c.id,c.type,c.sid,c.name,c.serviceId,c.begin_date,c.end_date,c.create_date,c.scan_type,c.status FROM (SELECT o.id,o.type,s.id as sid,s.name,o.serviceId,o.scan_type,o.begin_date,o.end_date,o.create_date,o.status FROM cs_order o,cs_service s WHERE o.userId = #{userId} AND o.serviceId = s.id) c WHERE 1=1 AND c.type = #{type}
AND c.sid = #{sid}
AND c.begin_date >= #{begin_date}
AND ((c.end_date <=#{end_date} AND c.type= 1) OR (c.begin_date <=#{end_date} AND type=2))
AND c.begin_date > #{currentDate}
AND c.begin_date <= #{currentDate} AND c.status in (0,3)
AND c.status in (1,2)
) b order by b.create_date desc
