mapper.select:
SELECT a.id orderId, a.serverName, b.astName, a.price, a.serviceId, a.isAPI, a.status, a.begin_date beginDate, a.end_date endDate FROM (SELECT co.*, cs.name serverName FROM cs_order co, cs_service cs WHERE co.serviceId = cs.id AND co.userId = #{userId} AND co.payFlag = #{payFlag} AND (co.isAPI = 0 or co.isAPI = 2)) a, (SELECT GROUP_CONCAT(ast.name) AS astName, cat.orderId FROM cs_asset ast, cs_order_asset cat WHERE ast.id IN(SELECT cas.assetId FROM cs_order_asset cas, cs_order co WHERE co.userId = #{userId} AND co.payFlag = #{payFlag} AND cas.orderId = co.id) AND ast.id = cat.assetId GROUP BY cat.orderId) b WHERE a.id = b.orderId
and a.id=#{orderId}
ORDER BY a.create_date desc

SELECT a.id orderId, a.serverName, a.price, b.package_type pack_type, a.serviceId, a.isAPI, a.status, b.buynum, a.begin_date beginDate, a.end_date endDate FROM (SELECT co.*, cs.name serverName FROM cs_order co, cs_service_api cs WHERE co.serviceId = cs.id AND co.userId =#{userId} AND co.payFlag = #{payFlag} AND co.isAPI = 1) a, (SELECT api.id orderId, api.package_type, api.buynum FROM cs_order_api api WHERE api.id in (SELECT co.id FROM cs_order co WHERE co.userId =#{userId} AND co.payFlag =#{payFlag} AND co.isAPI = 1)) b WHERE a.id = b.orderId
and a.id=#{orderId}
ORDER BY a.create_date desc

SELECT a.id orderId, a.serverName, a.price, a.serviceId, a.isAPI, a.status, a.scan_type, a.begin_date beginDate, a.end_date endDate, a.num buynum FROM (SELECT co.*, cs.name serverName FROM cs_order co, cs_service cs WHERE co.serviceId = cs.id AND co.userId =#{userId} AND co.payFlag = 0 AND co.isAPI = 3) a
WHERE a.id=#{orderId}
ORDER BY a.create_date desc

select * from ( SELECT a.serverName, a.price, a.isAPI, b.astName, b.addr, a.id orderId, a.type orderType, a.begin_date beginDate, a.end_date endDate, a.serviceId, a.scan_type scanPeriod, a.websoc, a.num buynum, a.create_date createDate FROM (SELECT s.name serverName, r.* FROM cs_order r, cs_service s WHERE r.serviceId = s.id AND r.id IN
#{orderId}
AND (r.isAPI = 0 OR r.isAPI = 2 OR r.isAPI = 3) AND r.userId=#{userId} ) a, (SELECT GROUP_CONCAT(t.name) AS astName, GROUP_CONCAT(t.addr) AS addr, ca.orderId FROM cs_asset t, cs_order_asset ca WHERE t.id IN(SELECT a.assetId FROM cs_order_asset a WHERE a.orderId IN(SELECT o.id FROM cs_order o WHERE o.id IN
#{orderId}
)) AND t.id = ca.assetId AND t.userid=#{userId} GROUP BY ca.orderId) b WHERE a.id = b.orderId UNION
SELECT c.serverName, c.price, c.isAPI, d.astName, d.addr, c.id orderId, c.type orderType, c.begin_date beginDate, c.end_date endDate, c.serviceId, c.scan_type scanPeriod, c.websoc, d.buynum, c.create_date FROM (SELECT s.name serverName, r.* FROM cs_order r, cs_service_api s WHERE r.serviceId = s.id AND r.id IN
#{orderId}
AND isAPI = 1 AND r.userId=#{userId} ) c, (SELECT CAST(i.package_type AS CHAR) AS astName, CAST(i.message AS CHAR) AS addr, i.id, i.buynum buynum FROM cs_order_api i WHERE i.id IN(SELECT o.id FROM cs_order o WHERE o.id IN
#{orderId}
) AND i.userId=#{userId} ) d WHERE c.id = d.id
Union
SELECT e.serverName, e.price, e.isAPI, 0, 0, e.id orderId, e.type orderType, e.begin_date beginDate, e.end_date endDate, e.serviceId, e.scan_type scanPeriod, e.websoc, e.num buynum, e.create_date createDate FROM (SELECT s.name serverName, r.* FROM cs_order r, cs_service s WHERE r.serviceId = s.id AND r.id IN
#{orderId}
AND r.isAPI = 3 AND r.userId=#{userId} ) e )res ORDER BY res.createDate desc

select count(*) from cs_order where userId =#{userId} and
id in 
	  
     #{orderId}

mapper.update:
 UPDATE  cs_order o 
 
	  
	    o.id=#{newOrderId}, 
	    o.create_date=#{createDate},
	 
	 
	 
	   o.status = #{status},
	 
	 
	   o.orderListId = #{orderListId}
	 
 
 WHERE o.isAPI=#{isAPI} AND o.id=#{orderId}

UPDATE cs_order o
o.id=#{newOrderId}, o.create_date=#{createDate},
o.status = #{status},
o.orderListId = #{orderListId},
o.remarks = #{remarks}
WHERE o.isAPI=#{isAPI} AND o.id=#{orderId}

update cs_order_list
set
 pay_date = #{pay_date}
where
 id = #{id}

update cs_order
set
	begin_date = #{beginDate},
	end_date = #{endDate}
where
	id= #{orderId}

update cs_order_api
set
	begin_date = #{beginDate},
	end_date = #{endDate}
where
	id= #{orderId}

update cs_order
set
	orderListId = #{newOrderIds}
where
	id= #{newOrderIds}

mapper.select:
        SELECT s.*,c.name serviceName,GROUP_CONCAT(a.name) assetName,
 GROUP_CONCAT(a.addr) assetAddr from cs_settlemet s, cs_service c,cs_asset a
        
        WHERE s.userId = #{userId}
    AND s.id = #{id}
AND s.serviceId=c.id

AND a.id IN    
          #{assetId}

        SELECT * from cs_settlemet s
        WHERE s.userId = #{userId}
    AND s.id = #{id}

        SELECT s.*,api.name serviceName FROM cs_settlemet s, cs_service_api api
        
        WHERE s.userId =#{userId}
    AND s.id = #{id}
AND s.serviceId=api.id
