mapper.select:
SELECT count(*) count,service_type,api_type FROM `cs_api`  a  where a.service_type=#{serviceType}

	AND a.create_time >= #{beginTime}
	 

	AND a.create_time <=#{endTime}

 GROUP BY a.api_type

		SELECT count(*) count1, `user`.`name`,api.apikey FROM `cs_user` user, cs_api api where `user`.apikey=api.apikey
		
			AND api.create_time >= #{beginTime}
			 
		
			AND api.create_time <=#{endTime}
		

		 GROUP BY api.apikey ORDER BY count1 DESC

		SELECT count(*) count1, `user`.`name`,api.apikey FROM `cs_user` user, cs_api api where `user`.apikey=api.apikey
		
			AND api.create_time >= #{beginTime}
			 
		
			AND api.create_time <=#{endTime}
		

		 GROUP BY api.apikey ORDER BY count1 DESC limit 5;

SELECT count(*) count,service_type FROM `cs_api` a,cs_user user where `user`.apikey=a.apikey 

	and `user`.`name`=#{userName}


	and a.create_time >= #{beginTime}
	 

	AND a.create_time <=#{endTime}
	
and a.service_type <> 100
GROUP BY a.service_type

SELECT count(*) counts,service_type, case when DATE_FORMAT(create_time,'%H:%i') > '00:00' and DATE_FORMAT(create_time,'%H:%i') <= '04:00' then '00:00-04:00' when DATE_FORMAT(create_time,'%H:%i') > '04:00' and DATE_FORMAT(create_time,'%H:%i') <= '08:00' then '04:00-08:00' when DATE_FORMAT(create_time,'%H:%i') > '08:00' and DATE_FORMAT(create_time,'%H:%i') <= '12:00' then '08:00-12:00' when DATE_FORMAT(create_time,'%H:%i') > '12:00' and DATE_FORMAT(create_time,'%H:%i') <= '16:00' then '12:00-16:00' when DATE_FORMAT(create_time,'%H:%i') > '16:00' and DATE_FORMAT(create_time,'%H:%i') <= '20:00' then '16:00-20:00' else '20:00-24:00' end AS timevalue FROM `cs_api` WHERE service_type <> 100
and create_time >= #{beginTime}
AND create_time <=#{endTime}
GROUP BY service_type,timevalue ORDER BY service_type,timevalue

SELECT count(*) counts,a.service_type, case when DATE_FORMAT(a.create_time,'%H:%i') > '00:00' and DATE_FORMAT(a.create_time,'%H:%i') <= '04:00' then '00:00-04:00' when DATE_FORMAT(a.create_time,'%H:%i') > '04:00' and DATE_FORMAT(a.create_time,'%H:%i') <= '08:00' then '04:00-08:00' when DATE_FORMAT(a.create_time,'%H:%i') > '08:00' and DATE_FORMAT(a.create_time,'%H:%i') <= '12:00' then '08:00-12:00' when DATE_FORMAT(a.create_time,'%H:%i') > '12:00' and DATE_FORMAT(a.create_time,'%H:%i') <= '16:00' then '12:00-16:00' when DATE_FORMAT(a.create_time,'%H:%i') > '16:00' and DATE_FORMAT(a.create_time,'%H:%i') <= '20:00' then '16:00-20:00' else '20:00-24:00' end AS timevalue FROM `cs_api` a left join cs_user user on `user`.apikey=a.apikey where a.service_type <> 100
and a.create_time >= #{beginTime}
AND a.create_time <=#{endTime}
and `user`.`name`=#{userName}
GROUP BY service_type,timevalue ORDER BY service_type,timevalue
