mapper.select:
select s.name,s.serviceId, date_format(s.begin_date,'%Y-%m-%d %H:%i:%s') begin_date,date_format(s.end_date,'%Y-%m-%d %H:%i:%s') end_date,as1.level,s.orderId,count(1) num from (select a.group_id,a.taskId,a.id,a.level from cs_alarm a)as1, (select t.group_id,t.taskId, ast.name,o.begin_date,o.end_date,o.serviceId,o.id orderId from cs_task t,cs_order_asset oa,cs_order o, cs_asset ast where t.order_asset_Id=oa.id and oa.orderId=o.id and oa.assetId=ast.id and o.status in(1,2)
and ast.name like CONCAT('%','${assertName}','%')
and o.serviceId=#{serverId}
and date_format(o.begin_date,'%Y-%m-%d')=date_format(#{begin_date},'%Y-%m-%d')
and date_format(o.end_date,'%Y-%m-%d')=date_format(#{end_date},'%Y-%m-%d')
and o.id=#{orderCode}
) s where (as1.taskid=s.taskId or as1.group_id=s.taskId)
and as1.level=#{alarmRank}
group by s.name,s.serviceId,s.begin_date ,s.end_date,as1.level,s.orderId
