mapper.select:
SELECT count(DISTINCT userId) FROM cs_login_history WHERE

	loginTime>=#{begin_date} AND loginTime<=#{end_date}
	
and (userType=2 or userType=3  )

SELECT lh.userId, COUNT(lh.userId) count,u.name from cs_login_history lh, cs_user u WHERE u.id=lh.userId AND
lh.loginTime>=#{begin_date} AND lh.loginTime<=#{end_date}
and (lh.userType=2 or lh.userType=3 ) GROUP BY lh.userId ORDER BY count desc LIMIT 10

SELECT DATE_FORMAT(loginTime,'%H:00:00') AS time,DATE_FORMAT(DATE_ADD(loginTime,INTERVAL 1 hour),'%H:00:00')as time1,count(*) as count FROM cs_login_history
WHERE loginTime>=#{begin_date} AND loginTime<=#{end_date}
group by HOUR(time) ORDER BY count desc LIMIT 5
