	      FROM cs_order o,cs_service s
	      WHERE o.userId = #{userId} AND o.serviceId = s.id and o.status in (1,2) order by o.create_date desc) c 
	WHERE
		1=1  
		
		
			AND c.type = #{type}
		
			
			AND c.sid = #{servName} 
			 
		
