mapper.select:
select * from cs_alarm
WHERE 1 = 1

	AND name like #{name}


	AND taskId=#{taskId}


	AND alarm_type=#{alarm_type}


	AND level=#{level}

	        select a.id,a.name,a.alarm_time,a.score,a.level,a.alarm_content,a.url,a.keyword,a.taskId,a.alarm_type,a.userId,a.group_id,a.advice from cs_alarm a,
			  (select t.taskId,t.group_id from cs_order_asset oa,cs_task t 
			    where oa.id = t.order_asset_id 
			    and oa.orderId=#{orderId}
			    and t.order_asset_id = #{orderAssetId}
			    and t.status = 3
			    
	            and t.group_flag=#{group_flag}
	            
			    order by t.execute_time desc
			    
	             limit 1 
	            ) b
			where (a.taskId = b.taskId or a.group_id = b.group_id)
			
	            and a.level=#{level}
	        
	        
                and a.name=#{name}
            
	        order by a.level desc

    select * from cs_alarmhuawei a
	where a.taskId =(select t.taskId from cs_order_ip op,cs_taskhuawei t 
		    where op.id = t.order_ip_id 
		    and op.orderId=#{orderId}
		    and t.status = 3
		    order by t.execute_time desc
		    )  and a.attack_type=#{type}
	order by a.alarm_time desc

SELECT d.serviceId as alarm_type,COUNT(d.serviceId) AS count from (select e.id,e.alarm_time,e.level,e.serviceId from (select a.id,a.alarm_time,a.level,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_alarm a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.taskId=t.taskId or a.group_Id = t.group_Id) union select a.id,a.warn_time as alarm_time,0 as level,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_taskwarn a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.task_id=t.taskId or a.group_Id = t.group_Id)) e where 1=1
AND e.alarm_time >= #{begin_datevo}
AND e.alarm_time <=#{end_datevo}
AND e.level=#{level}
) d GROUP BY d.serviceId

SELECT d.level,COUNT(d.level) AS count from (select e.id,e.alarm_time,e.level,e.serviceId from (select a.id,a.alarm_time,a.level,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_alarm a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.taskId=t.taskId or a.group_Id = t.group_Id) union select a.id,a.warn_time as alarm_time,0 as level,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_taskwarn a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.task_id=t.taskId or a.group_Id = t.group_Id)) e WHERE 1=1 and e.serviceId = #{alarm_type}
AND e.alarm_time >= #{begin_datevo}
AND e.alarm_time <=#{end_datevo}
) d GROUP BY d.level

SELECT d.name as alarm_type, COUNT(d.name) AS count from (select e.id,e.alarm_time,e.level,e.name,e.serviceId from (select a.id,a.alarm_time,a.level,a.name,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_alarm a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.taskId=t.taskId or a.group_Id = t.group_Id) union select a.id,a.warn_time as alarm_time,0 as level,a.name,o.serviceId from cs_order o,cs_order_asset oa,cs_task t,cs_taskwarn a where o.id = oa.orderId and t.order_asset_Id = oa.id and (a.task_id=t.taskId or a.group_Id = t.group_Id)) e WHERE 1=1 and e.serviceId = #{alarm_type} and e.level = #{level}
AND e.alarm_time >= #{begin_datevo}
AND e.alarm_time <=#{end_datevo}
) d GROUP BY d.name ORDER BY COUNT(d.name) DESC LIMIT 10

mapper.delete:
delete from cs_alarm where 

 taskId = #{taskId}


 group_id = #{group_id}

mapper.select:
select a.name,a.level,count(*) as num from cs_alarm a,
    (select t.taskId,t.group_id from cs_order_asset oa,cs_task t 
    where oa.id = t.order_asset_id
    and oa.orderId=#{orderId}
    and t.order_asset_id = #{orderAssetId}
    and t.status = 3
    
    and t.group_flag=#{group_flag}
    
    order by t.execute_time desc
    
     limit 1   
    
    ) b  where (a.taskId = b.taskId or a.group_id = b.group_id) group by a.name order by a.level desc

	        select a.id,a.name,a.alarm_time,a.score,a.level,a.alarm_content,a.url,a.keyword,a.taskId,a.alarm_type,a.userId,a.group_id,a.advice from cs_alarm a,
			  (select t.taskId,t.group_id from cs_order_asset oa,cs_task t 
			    where oa.id = t.order_asset_id 
			    and oa.orderId=#{orderId}
			    and t.order_asset_id = #{orderAssetId}
			    and t.status = 3
			    
	            and t.group_flag=#{group_flag}
	            
			    order by t.execute_time desc
			    
	             limit 1   
	            ) b
			where (a.taskId = b.taskId or a.group_id = b.group_id)
			
	            and a.level=#{level}
	        
	        
                and a.name=#{name}
            
	        order by a.level desc

	        select COUNT(a.level) AS count,a.level from cs_alarm a,
			  (select t.taskId,t.group_id from cs_order_asset oa,cs_task t 
			    where oa.id = t.order_asset_id 
			    and oa.orderId=#{orderId}
			    and t.order_asset_id = #{orderAssetId}
			    and t.status = 3
			    
	            and t.group_flag=#{group_flag}
	            
			    order by t.execute_time desc
			    
	             limit 1   
	            ) b
			where (a.taskId = b.taskId or a.group_id = b.group_id)
			
	            and a.level=#{level}
	        
	        
                and a.name=#{name}
            
            group by a.level
	        order by a.level desc
