select.if:
 
         select count(*) count from cs_alarm a
         	where a.serviceId = #{serviceId}
		    and DATE_FORMAT(a.alarm_time,'%Y.%m') = #{month}

   
         select count(*) count from cs_taskwarn a
         	where a.serviceId = #{serviceId}
		    and DATE_FORMAT(a.warn_time,'%Y.%m') = #{month}

mapper.select:
         select  a.name,COUNT(a.name) AS count from cs_alarm a
		 where a.serviceId=1 and a.name <> '' and a.alarm_time >= #{beginDate} and a.alarm_time <= #{endDate}
         group by a.name

         select  COUNT(a.alarm_time) count,a.level,DATE_FORMAT(a.alarm_time,'%Y.%m') month1 from cs_alarm a
		 where a.serviceId=1 and DATE_FORMAT(a.alarm_time,'%Y.%m') >= #{month}
         group by level,month1

SELECT order1.serviceId,COUNT(order1.serviceId) count,DATE_FORMAT(order1.begin_date,'%Y.%m') month1 FROM `cs_order` order1
 WHERE DATE_FORMAT(order1.begin_date,'%Y.%m')=#{month}
GROUP BY month1,order1.serviceId
