mapper.select:
   SELECT c.id,c.type,c.sid,c.name,c.begin_date,c.end_date,c.create_date,c.scan_type
	 FROM (SELECT o.id,o.type,s.id as sid,s.name,o.scan_type,o.begin_date,o.end_date,o.create_date
	      FROM cs_order o,cs_service s
	      WHERE o.userId = #{userId} AND o.serviceId = s.id and o.status in (1,2) order by o.create_date desc) c 
	WHERE
		1=1  
		
		
			AND c.type = #{type}
		
			
			AND c.sid = #{servName} 
			 
		
			AND c.begin_date >= #{begin_date}
		
		
			AND ((c.end_date <=#{end_date} AND c.type= 1) OR (c.begin_date <=#{end_date} AND type=2))

SELECT c.id,c.type,c.sid,c.name,c.begin_date,c.end_date,c.create_date,c.scan_type
FROM (SELECT o.id,o.type,s.id as sid,s.name,o.scan_type,o.begin_date,o.end_date,o.create_date
      FROM cs_order o,cs_service s
      WHERE o.userId = #{userId} AND o.serviceId = s.id and o.status in (1,2) order by o.create_date desc) c 
WHERE
    1=1  
    
        AND c.type = #{type}
    
     
        AND c.sid = #{servName} 
        
    
        AND c.begin_date >= #{begin_date}
        
    
      AND ((c.end_date <=#{end_date} AND c.type= 1) OR (c.begin_date <=#{end_date} AND type=2))
    
    limit #{pageNow},#{pageSize}

        		SELECT DISTINCT b.name as name ,SUM(b.count) as count 
        		FROM(
					SELECT c.name,COUNT(c.name) as count
					FROM (SELECT s.name
						  FROM cs_order o,cs_service s
						  WHERE 1=1 
						  and o.serviceId=s.id
						  
							AND o.type = #{type}
						  
						 
			                AND o.type = 1
			                AND o.begin_date >= #{currentDate}
			             
			             
			                AND o.type = 1
			                AND o.begin_date < #{currentDate}
			             
						
						
						
                            AND o.begin_date >= #{begin_date}
                            AND o.begin_date <=#{end_date}            
                            
                        
                            AND o.begin_date >= #{begin_date}                            
                        
			            
			              AND o.begin_date <= #{end_date}                                             
                        
							
							AND s.name = #{servName} 
						
						  ) c 
					GROUP BY c.name
			        UNION
			          SELECT c.name,COUNT(c.name) as count
						FROM (SELECT s.name
							  FROM cs_order o,cs_service s
							  WHERE 
							  1=1
							  and o.serviceId=s.id 
							  
								AND o.type = #{type}
							  
							  
			                AND o.type = 2
			                AND o.begin_date >= #{currentDate}
			            
			            
			                AND o.type = 2
			                AND o.begin_date < #{currentDate}
			            
			            
						
						
                            AND o.begin_date >= #{begin_date}
                            AND o.begin_date <=#{end_date}            
                            
                        
                            AND o.begin_date >= #{begin_date}                            
                        
                        
                          AND o.begin_date <= #{end_date}                                             
                        
							
							AND s.name = #{servName} 
						
							  ) c 
					  GROUP BY c.name
			  ) b
			  GROUP BY b.name

SELECT IFNULL(COUNT(o.id),0)  
FROM cs_order o, cs_order_asset oa, cs_task t
WHERE o.id=oa.orderId AND oa.id=t.order_asset_Id AND o.id=#{orderId}

mapper.update:
		update cs_order
		
			
				message=#{message},
			
			
                status=#{status},
            
            
                delFlag=#{delFlag},
            
            
                websoc=#{websoc}
            
		
		where id=#{id}
