mapper.select:
SELECT o.id,o.type,s.id as sid,s.name,o.serviceId,o.scan_type,o.begin_date,o.end_date,o.create_date,o.status
      FROM cs_order o,cs_service s
      WHERE o.userId = #{userId} AND o.serviceId = s.id
    
        AND o.type = #{type}
    
     
        AND s.id = #{servName} 
        
    
        AND o.begin_date >= #{begin_date}
        
    
    AND ((o.end_date <=#{end_date} AND o.type= 1) OR (o.begin_date <=#{end_date} AND type=2))
   
    
    
        AND o.begin_date > #{currentDate}
    
    
        AND o.begin_date <= #{currentDate}
        AND o.status in (0,3)
    
    
        
        AND o.status in (1,2)
    
    
        AND o.id like CONCAT(CONCAT('%', #{search}), '%')
    
    order by o.create_date desc

		SELECT o.id,o.type,s.id as sid,s.name,o.serviceId,o.scan_type,o.begin_date,o.end_date,o.create_date,o.status
        FROM cs_order o,cs_service s
        WHERE o.userId = #{userId} AND o.serviceId = s.id
            
                AND o.type = #{type}
            
             
                AND s.id = #{servName} 
                
            
                AND o.begin_date >= #{begin_date}
                
            
                AND ((o.end_date <=#{end_date} AND o.type= 1) OR (o.begin_date <=#{end_date} AND type=2))
            
            
                AND o.begin_date > #{currentDate}
            
            
                AND o.begin_date <= #{currentDate}
                AND o.status in (0,3)
            
            
                
                AND o.status in (1,2)
            
            
                AND o.id like CONCAT(CONCAT('%', #{search}), '%')
            
            order by o.create_date desc limit #{pageNow},#{pageSize}

select b.* from (select o.id,o.type,s.name,o.serviceId,o.begin_date,o.end_date,o.create_date,o.scan_type,o.status,o.websoc,o.payFlag,o.isAPI, 0 as package_type,0 as num,o.delFlag,a.addr,a.name as addrName from cs_order o,cs_service s,cs_order_asset oa,cs_asset a where o.userId = #{userId} and o.serviceId = s.id and isAPI != 1 and o.id = oa.orderId and oa.assetId = a.id union select o.id,o.type,s.name,o.serviceId,o.begin_date,o.end_date,o.create_date,o.scan_type,o.status,o.websoc,o.payFlag,o.isAPI,oa.package_type,oa.num,o.delFlag,null as addr,null as addrName from cs_order o,cs_service_api s,cs_order_api oa where o.userId = #{userId} and o.serviceId = s.id and isAPI = 1 and oa.id = o.id) b where 1=1
AND b.type = #{type} AND b.isAPI = 0
AND b.name = #{servName}
AND b.begin_date >= #{begin_date}
AND ((b.end_date <=#{end_date} AND b.type= 1) OR (b.begin_date <=#{end_date} AND type=2))
AND b.begin_date > #{currentDate}
AND b.begin_date <= #{currentDate} AND b.status in (0,3)
AND b.status in (1,2)
and (b.addrName like CONCAT(CONCAT('%', #{search}), '%') or b.addr like CONCAT(CONCAT('%', #{search}), '%'))
order by b.create_date desc limit #{pageNow},#{pageSize}
