mapper.select:
select * from cs_task t
where t.status = #{status}

  and t.execute_time between date_add(now(),interval -1 minute) and now()

select * from cs_task t
where t.status = #{status} 

  and t.end_time between date_add(now(),interval -1 minute) and now()

select * from cs_task t where t.status = #{status}
and t.engine = -1
order by t.execute_time

select min(t.taskId) as taskId, t.status, date_format(t.execute_time,'%Y-%m-%d %H:%i:%S') executeTime, date_format(t.begin_time,'%Y-%m-%d %H:%i:%S') beginTime, ifnull(date_format(t.end_time,'%Y-%m-%d %H:%i:%S'),'') endTime, date_format(t.group_flag,'%Y-%m-%d %H:%i:%S') groupFlag, t.engineIP, t.taskProgress, t.currentUrl, t.scanTime, t.issueCount, t.requestCount, t.urlCount, t.averResponse, t.averSendCount, t.sendBytes, t.receiveBytes, t.group_id, ot.url from cs_task t,cs_order_task ot where t.orderTaskId = ot.orderTaskId and ot.orderId = #{orderId}
and t.status = #{status}
group by t.group_flag,ot.orderTaskId
