-- ----------------------------
-- Procedure structure for `pro_dayInYear`
-- ----------------------------
DROP PROCEDURE IF EXISTS `pro_dayInYear`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `pro_dayInYear`(IN `start_date` datetime,IN `end_date` datetime)
BEGIN

set @i = -1;

set @sql = repeat(" select 1 union all",-datediff(start_date,end_date)+1);

set @sql = left(@sql,length(@sql)-length(" union all"));
set @startDate = start_date;

set @sql = concat("select DATE_FORMAT(date_add(@startDate,interval @i:=@i+1 day),'%Y-%m-%d') as dayName from (",@sql,") as tmp");

prepare stmt from @sql;
execute stmt;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for `pro_getOrderForOneYear`
-- ----------------------------
DROP PROCEDURE IF EXISTS `pro_getOrderForOneYear`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `pro_getOrderForOneYear`(IN `start_date` datetime,IN `end_date` datetime,IN `serviceId` int)
BEGIN
	
set @i = -1;

set @sql = repeat(" select 1 union all",-datediff(start_date,end_date)+1);

set @sql = left(@sql,length(@sql)-length(" union all"));
set @startDate = start_date;
set @serviceId = serviceId;

set @sql = concat("select DATE_FORMAT(date_add(@startDate,interval @i:=@i+1 day),'%Y-%m-%d') as dayName from (",@sql,") as tmp");
set @sql = concat("SELECT dayName,if(isnull(m.count1),0,m.count1) count2 from (",@sql,") as tmp LEFT JOIN
		(SELECT DATE_FORMAT(order1.create_date,'%Y-%m-%d') date1, count(order1.create_date) count1 from cs_order order1 
		WHERE order1.create_date>=DATE_SUB(NOW(),INTERVAL 1 YEAR) and order1.serviceId=@serviceId
		GROUP BY  date1)m
		ON tmp.dayName=m.date1");
prepare stmt from @sql;
execute stmt;

END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for `pro_lastdayForMonth`
-- ----------------------------
DROP PROCEDURE IF EXISTS `pro_lastdayForMonth`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `pro_lastdayForMonth`(IN `start_date` datetime,IN `end_date` datetime)
BEGIN

set @i = -1;

set @sql = repeat(" select 1 union all",-datediff(start_date,end_date)+1);

set @sql = left(@sql,length(@sql)-length(" union all"));
set @startDate = start_date;

set @sql = concat("select DATE_FORMAT(date_add(@startDate,interval @i:=@i+1 day),'%Y-%m-%d') as dayName from (",@sql,") as tmp");

set @sql = concat("SELECT DATE_FORMAT((if(day(last_day(dayName))=30,LAST_DAY(dayName)-15,LAST_DAY(dayName)-16)),'%Y-%m-%d') lastday,DATE_FORMAT(dayName,'%Y-%m') months  from (",@sql,") as tmp GROUP BY months");

prepare stmt from @sql;
execute stmt;

END
;;
DELIMITER ;
